import React, { useState, useEffect } from 'react';

function ContadorCarrito(props) {
    return <h1 id='numCarro'>{props.numero}</h1>;
}

function App() {
    const [productos, setProductos] = useState([]);

    useEffect(() => {
        const productosElegidos = window.localStorage.getItem("productosElegidos");
        const productosElegidosParse = JSON.parse(productosElegidos);
        if (productosElegidosParse) {
            setProductos(productosElegidosParse);
        }
    }, []);

    useEffect(() => {
        window.localStorage.setItem("productosElegidos", JSON.stringify(productos));
    }, [productos]);

    return (
        <div>
            <ContadorCarrito numero={productos.length} />
            {/* Aquí debes agregar el código para mostrar los productos del carrito */}
        </div>
    );
}

export default App;
