import React, { useState, useEffect} from 'react'
import { useParams } from 'react-router-dom';
import Carousel from 'react-bootstrap/Carousel';
import Card from 'react-bootstrap/Card';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const productosElegidos = [];
const botonAgregar = document.querySelector("#botonAgregar")


function agregado() {
    botonAgregar.textContent = "I have changed!";;
} setInterval(agregado, 1000)


const  Producto = () => {

    const notify = () => toast.success('Agregado!', {
        position: "top-right",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
        });
    const [cardsProducts, setCardsProducts] = useState([])
    const { id } = useParams();

    useEffect(() => {
        
        fetch('https://res.cloudinary.com/dsdicaf5h/raw/upload/v1677544978/cenicero/productos_ugha4n.json')
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(dataJson => setCardsProducts(dataJson.results))
        .catch(error => console.log('Error fetching data: ', error));
    }, []);

    const productoFiltrado = cardsProducts.filter(product => String(product.id) === id )

    return (
        <div key={id} id='detalleProducto'>
            {
            productoFiltrado.map(product =>(
                <React.Fragment key={product.id}>
                    <div key={id} id='carousel'>
                        <Carousel variant="dark">
                            <Carousel.Item>
                                <img
                                className="d-block w-100"
                                src={product.img1}
                                alt="First slide"
                                />
                            </Carousel.Item>
                            <Carousel.Item>
                                <img
                                className="d-block w-100"
                                src={product.img2}
                                alt="Second slide"
                                />
                            </Carousel.Item>
                        </Carousel>
                    </div>
                    <div>
                    <Card>
                        <Card.Body>
                            <Card.Title id='nombre' className="shadow-lg p-3 mb-5 bg-white rounded">{product.nombre}</Card.Title>
                            <Card.Subtitle id='talle' className="mb-2 text-muted">Talle {product.talle}</Card.Subtitle>
                            <Card.Text id='medidas'>
                            {product.medidas}
                            </Card.Text>
                            <Card.Text id='precio' className="shadow-lg p-3 mb-5 bg-white rounded">
                            ${product.precio}
                            </Card.Text>
                            <button id='botonAgregar' onClick={()=>{
                                notify()
                                
                                // generamos la funcion de agregar producto
                                const agregarProducto = (id) =>{
                                    const productoEnCarrito = productosElegidos.find((prod) =>{
                                        return prod.id == product.id
                                    });;
                                    
                                    // si ya existe actualizamos cantidadElegida
                                    if (productoEnCarrito){
                                            let productoParaActualizar = productosElegidos.find((prod) =>{
                                            return prod.id == product.id
                                        });
                                            let precioParaActualizar = productoParaActualizar.precio;
                                            productoParaActualizar.elegidos = productoParaActualizar.elegidos + 1;
                                            productoParaActualizar.precioSubTotal = productoParaActualizar.elegidos * precioParaActualizar;
                                            console.log(productosElegidos);
                                            console.log(productoParaActualizar);
                                    }
                                    // si el elemento no existe ya en el array productosElegidos que....
                                    else{
                                        productosElegidos.push({
                                            id : product.id,
                                            nombre : product.nombre,
                                            precio : product.precio,
                                            img1 : product.img1,
                                            img2 : product.img2,
                                            precioSubTotal : product.precioSubTotal,
                                            elegidos : 1,
                                            medidas : product.medidas
                                            });
                                            console.log("click")
                                            console.log(productosElegidos);
                                    };
                                };
                                agregarProducto(); 
                            }} className='btn btn-outline-primary'>agregar</button>
                            <ToastContainer />
                        </Card.Body>
                    </Card>
                </div>
                </React.Fragment>
            ))
            };
        </div>
    )
};



export default Producto
