import React, { useState, useEffect} from 'react'
import { useParams } from 'react-router-dom';
import Carousel from 'react-bootstrap/Carousel';
import Card from 'react-bootstrap/Card';

const Producto = () => {

    const [cardsProducts, setCardsProducts] = useState([])
    const { id } = useParams();

    useEffect(() => {
        
        fetch('https://res.cloudinary.com/dsdicaf5h/raw/upload/v1676644291/cenicero/productos_vjfpuo.json')
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(dataJson => setCardsProducts(dataJson.results))
        .catch(error => console.log('Error fetching data: ', error));
    }, []);

    const productoFiltrado = cardsProducts.filter(product => String(product.id) === id )

    return (
        <div id='detalleProducto'>
            {
            productoFiltrado.map(product =>(
                <>
                <div id='carousel'>
                    <Carousel variant="dark">
                        <Carousel.Item>
                            <img
                            className="d-block w-100"
                            src={product.img1}
                            alt="First slide"
                            />
                        </Carousel.Item>
                        <Carousel.Item>
                            <img
                            className="d-block w-100"
                            src={product.img2}
                            alt="Second slide"
                            />
                        </Carousel.Item>
                    </Carousel>
                </div>
                <div>
                <Card>
                    <Card.Body>
                        <Card.Title>{product.nombre}</Card.Title>
                        <Card.Subtitle className="mb-2 text-muted">Talle {product.talle}</Card.Subtitle>
                        <Card.Text>
                        {product.precio}
                        </Card.Text>
                        <Card.Link href="#">Card Link</Card.Link>
                        <Card.Link href="#">Another Link</Card.Link>
                    </Card.Body>
                </Card>
                </div>
                </>
            ))
            }
        </div>
    )
}

export default Producto
