import React , { useState, useEffect } from 'react';


function ContadorCarrito(props) {
    const numero = props.productosElegidosParse ? props.productosElegidosParse.length : 0;
    return <h1 id='numCarro'>{props.numero}</h1>;
}


const productosElegidos = window.localStorage.getItem("productosElegidos");
const productosElegidosParse = JSON.parse(productosElegidos)

const length = productosElegidosParse.length

if (length === null){
    console.log("carrito vacio")
}else{
    function App() {
        const [numeroObjetos, setNumeroObjetos] = useState(0);
    
        useEffect(() => {
            const productosElegidos = window.localStorage.getItem("productosElegidos");
            const productosElegidosParse = JSON.parse(productosElegidos);
            setNumeroObjetos(productosElegidosParse.length);
        }, []);
    
        useEffect(() => {
            // Este efecto se ejecutará cada vez que se actualice el número de objetos
            const productosElegidos = window.localStorage.getItem("productosElegidos");
            const productosElegidosParse = JSON.parse(productosElegidos);
            setNumeroObjetos(productosElegidosParse.length);
        }, [numeroObjetos]);
    
        return (
            <div>
                <ContadorCarrito numero={numeroObjetos} />
            </div>
        );
    }
    
}



export default App
