import React, { useState, useEffect } from 'react';

function ContadorCarrito(props) {
    return <h1 id='numCarro'>{props.numero}</h1>;
}


function App() {
    const [numProductos, setNumProductos] = useState(0);
    console.log("contador"+numProductos)
    useEffect(() => {
        const productosElegidos = window.localStorage.getItem("productosElegidos");
        const productosElegidosParse = JSON.parse(productosElegidos);

        if (productosElegidosParse === null) {
            console.log("carrito vacio");
            setNumProductos(0);
        } else {
            setNumProductos(productosElegidosParse.length);
        }
    }, []);

    return (
        <div>
            <ContadorCarrito numero={numProductos} />
        </div>
    );
}

export default App;
